import React from 'react';

function Comment({ id, name, comment, date }) {
  const formattedDate = new Date(date).toLocaleDateString();

  return (
    <li id={id}>
      <h2>{name}</h2>
      <p>{comment}</p>
      <date>{formattedDate}</date>
    </li>
  );
}
export default Comment;
