import React from 'react';
import Form from '../Form';
import GetComments from './GetComments';

function AppendMessages(messageArr) {
  //Trying to sort the comment before they are displayed
  console.log('I am just about to sort my messages Array');
  const sortedArry = messageArr.sort(function (a, b) {
    return b.timestamp - a.timestamp;
  });

  //to sort the array before looping
  for (let i = 0; i < sortedArry.length; i++) {
    console.log('I am sorting through myArray ' + sortedArry[1]);

    let messageDate = new Date(sortedArry[i].timestamp);
    let messageComment = sortedArry[i].comment;

    console.log('I am sorting have appended my childElements to the DOM');
  }
  <>
    <p>{messageDate}</p>
    <p>{messageComment}</p>
  </>;
}
export default AppendMessages;
