import AppendMessages from './AppendMessages';
import axios from 'axios';

const commentURL = `https://project-1-api.herokuapp.com/comments/?api_key=f1ee7466-a255-4e43-b525-d1bb7750f7a8`;

function GetComments() {
  // connecting to the api and authinticating to down load data
  axios
    .get(commentURL)
    .then((response) => {
      // if the response is fullfilled move on to process data
      console.log('I am calling append messages');
      AppendMessages(response.data);
    })
    .catch((error) => {
      console.log(error);
    });
}
export default GetComments;
